// call through the array to draw the objects

function define(polygon,col) {
	ctx.fillStyle = col; 
	ctx.beginPath();
	var poly = polygon.split(",");
    ctx.moveTo(poly[0], poly[1]);
	var v = 1;
	var x = 0;
	var y = 0;
	for(p=2;p<poly.length;p++){
		if(v==1){
			x = poly[p];
			v=2;
		}else{
			y = poly[p];
			ctx.lineTo(x,y);
			v=1;
		}
	}  
    ctx.fill();
	
	ctx.shadowColor = "#fd4a1f";
	ctx.shadowOffsetX = 1; 
	ctx.shadowOffsetY = 1;
	
	ctx.strokeStyle="#fd4a1f";
	ctx.lineWidth = 2;
	ctx.stroke();
    ctx.closePath();
}   
function hitTest(polygon) { 
	define(polygon,"#fd4a1f");
    return (ctx.isPointInPath(mouseX, mouseY));
}

function handleMouseMove(e,polyArray) {
    e.preventDefault();
    mouseX = parseInt(e.clientX - offsetX);
    mouseY = parseInt(e.clientY - offsetY);

    // check if the mouse is inside the polygon
        var isInside = hitTest(polyArray);
        if (isInside) {	
			define(polyArray,"#fd4a1f");
        } else {
			define(polyArray,"#ffffff");
        }
}
